def TelefragSelectSpawnPoint(spot, *qwp_extra):
    e = engine.world
    firstspot = engine.world
    search_spot = 0
    search_spot = 25
    firstspot = spot
    while search_spot:
        e = qc.findradius(spot.origin, 50)
        if not e:
            search_spot = 0
        else:
            occupied = 0
            occupied = 0
            while not occupied and not (not e):
                if e.classname == 'player' and e.deadflag == defs.DEAD_NO:
                    occupied = 1
                else:
                    e = e.chain
                
            if occupied:
                spot = client.SelectSpawnPoint()
                search_spot -= 1
                if spot == firstspot:
                    search_spot = 0
                
            else:
                search_spot = 0
            
        
    return (spot)
    
